mapper.select:
    select 
        id as id, 
        event_name as eventName,
        ip_addr as ipAddr,
        ip_source as ipSource,
        device as device, 
        created_date as createdDate
    from
        recvice_ip
    
        
            id = #{id}
        
        
            ip_addr = #{ipAddr}
        
        
            ip_source = #{ipSource}
        
        
            device = #{device}
        
        
           created_date <  #{startDate}
        
        
           created_date >  #{endDate}
        
        
          created_date between #{startDate} and #{endDate}
